import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { booksByGenre } from "../Api";


const Card = ({ book }) => {
  const navigate = useNavigate();

  const handleCardClick = () => {
    navigate(`/book/${encodeURIComponent(book.id)}`);
  };

  return (
    <div className="card" onClick={handleCardClick}>
      <div className="img-container">
        <img 
          className="card-img-top" 
          src={book.image || "https://via.placeholder.com/150"} 
          alt="Book cover" 
        />
      </div>
      <div className="card-body">
        <h5 className="card-title">{book.title}</h5>
        <p className="card-text">
          <strong>By: </strong>{(book.authors && book.authors.length > 0) ? book.authors[0] : "Not available"}<br></br>
          <strong>Genre: </strong>{(book.categories)}
        </p>
      </div>
    </div>
  );
};


function getRandomEntries(obj, count) {
  const entries = Object.entries(obj);
  const shuffled = [...entries].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
}

export function TenCards() {
  const [randomCards, setRandomCards] = useState([]);

  useEffect(() => {
    const selected = getRandomEntries(booksByGenre, 5);
    setRandomCards(selected);
  }, []);

  return (
    <>
    <div>
      {randomCards.map(([key, card]) => (
        <div key={key}>
          <Card book={card}/>
        </div>
      ))}
    </div>
    </>
  );
}


export default Card;
