import React, { useState } from 'react';
import Navbar from '../Components/Navbar';
import { useUser } from '../UserContext';
import UserCart from '../Components/UserCart';
import './Cart.css';

function Cart() {
    const { user, setUser } = useUser();
    const books = Array.isArray(user?.books) ? user.books : [];
    const [applyPoints, setApplyPoints] = useState(false);
    const [showModal, setShowModal] = useState(false);

    // State to store user info for checkout
    const [userInfo, setUserInfo] = useState({
        firstName: '',
        lastName: '',
        email: '',
        address: '',
    });

    const handleQuantityChange = (index, delta) => {
        const updatedBooks = [...books];
        const currentQty = updatedBooks[index].quantity || 1;
        const newQty = Math.max(1, currentQty + delta);
        updatedBooks[index].quantity = newQty;
        setUser({ ...user, books: updatedBooks });
    };

    const calculateTotalPrice = (book) => {
        const qty = book.quantity || 1;
        const price = book.price || 0;
        return qty * price;
    };

    const cartTotal = books.reduce((acc, book) => acc + calculateTotalPrice(book), 0);

    const getDiscountedTotal = () => {
        const discount = (user?.points || 0) / 100;
        const total = cartTotal - discount;
        return total > 0 ? total : 0;
    };

    // Function to open the modal
    const handleCheckoutClick = () => {
        setShowModal(true);
    };

    // Function to close the modal
    const closeModal = () => {
        setShowModal(false);
    };

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserInfo((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        // Normally, you would submit the form data here
        alert('Checkout complete with user info: ' + JSON.stringify(userInfo));
        closeModal();
    };

    return (
        <>
            <Navbar use="Guest" />
            <div style={{ display: 'flex', height: '100vh', overflow: 'hidden' }} id="body">
                <div style={{ width: '75%', overflowY: 'auto', padding: '1rem', paddingTop: '1rem' }} className="cart-container">
                    <h3 id="CardTitle">Your Cart</h3>
                    <div className="cart-items" style={{ marginTop: '-1rem' }}>
                        <UserCart />
                    </div>
                </div>

                <div style={{ width: '25%', display: 'flex', flexDirection: 'column', height: '100%', borderLeft: '1px solid #ccc' }}>
                    <div style={{ flex: '1', overflowY: 'auto', padding: '1rem' }}>
                        <h3 id="CardTitle">Cart Details</h3>
                        <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                            {books.map((book, index) => (
                                <li key={index} style={{ marginBottom: '1rem', padding: '0.5rem', borderRadius: '6px', backgroundColor: '#f9f9f9' }}>
                                    <p style={{ margin: 0, fontWeight: 'bold', marginBottom: '6px' }}>
                                        {book.title || 'Untitled'}
                                    </p>
                                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px', fontSize: '16px' }}>
                                        <span>Price: ${book.price?.toFixed(2) || '0.00'}</span>
                                        <span>Qty: {book.quantity || 1}</span>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>

                    <div style={{
                        borderTop: '1px solid #ccc',
                        padding: '1rem',
                        backgroundColor: '#333',
                        boxShadow: '0 -2px 5px rgba(0,0,0,0.05)',
                        color: 'white',
                    }}>
                        <h4>Total: ${applyPoints ? getDiscountedTotal().toFixed(2) : cartTotal.toFixed(2)}</h4>
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'space-between',
                            marginTop: '1rem',
                        }}>
                            <label htmlFor="applyPoints" style={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: '8px',
                                fontSize: '14px',
                            }}>
                                <input
                                    type="checkbox"
                                    id="applyPoints"
                                    name="applyPoints"
                                    style={{ transform: 'scale(1.2)' }}
                                    checked={applyPoints}
                                    onChange={() => setApplyPoints(!applyPoints)}
                                    disabled={user?.points <= 0}
                                />
                                Apply {user?.points || 0} Points
                            </label>
                        </div>
                        <button
                            onClick={handleCheckoutClick}
                            style={{
                                marginTop: '1rem',
                                padding: '0.75rem',
                                width: '100%',
                                backgroundColor: '#007bff',
                                color: 'white',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                fontWeight: 'bold',
                            }}
                        >
                            Checkout
                        </button>
                    </div>
                </div>
            </div>

            {showModal && (
                <div style={{
                    position: 'fixed',
                    top: '0',
                    left: '0',
                    zIndex: '10000',
                    width: '100%',
                    height: '100%',
                    backgroundColor: 'rgba(0, 0, 0, 0.5)',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                }} onClick={closeModal}>
                    <div
                        style={{
                            backgroundColor: 'white',
                            padding: '2rem',
                            borderRadius: '8px',
                            width: '300px',
                            textAlign: 'center',
                        }}
                        onClick={(e) => e.stopPropagation()}
                    >
                        <h4>Checkout Summary</h4>
                        <p>Total: ${applyPoints ? getDiscountedTotal().toFixed(2) : cartTotal.toFixed(2)}</p>
                        {applyPoints && <p>Discount Applied: ${((user?.points || 0) / 100).toFixed(2)}</p>}

                        <form onSubmit={handleSubmit}>
                            <div>
                                <label htmlFor="firstName">First Name</label>
                                <input
                                    type="text"
                                    id="firstName"
                                    name="firstName"
                                    value={userInfo.firstName}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label htmlFor="lastName">Last Name</label>
                                <input
                                    type="text"
                                    id="lastName"
                                    name="lastName"
                                    value={userInfo.lastName}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label htmlFor="email">Email</label>
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    value={userInfo.email}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label htmlFor="address">Address</label>
                                <input
                                    type="text"
                                    id="address"
                                    name="address"
                                    value={userInfo.address}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>

                            <button
                                type="submit"
                                style={{
                                    marginTop: '1rem',
                                    padding: '0.5rem 1rem',
                                    backgroundColor: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    cursor: 'pointer',
                                }}
                            >
                                Confirm Purchase
                            </button>
                        </form>
                    </div>
                </div>
            )}
        </>
    );
}

export default Cart;
