import { useUser } from '../UserContext';
import Card from './Card';

function UserCart() {
  const { user } = useUser();

  if (!user || !Array.isArray(user.books) || user.books.length === 0) {
    return <div>No books in cart.</div>;
  }

  return (
    <div style={{ padding: '1rem' }}>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
          gap: '1.5rem',
        }}
      >
        {user.books.map((book, index) => (
          <div key={index} style={{ textAlign: 'center' }}>
            <Card book={book} />
            <p style={{ margin: '0.5rem 0 0' }}><strong>Qty:</strong> {book?.quantity || 1}</p>
            <p style={{ margin: 0 }}><strong>Price:</strong> ${book?.price?.toFixed(2) || '0.00'}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default UserCart;
