import { booksByGenre } from "./Api";

export function GetBookByID(bookID) {
  // Iterate over the booksByGenre object to find a book by its ID
  for (const books of Object.values(booksByGenre)) {
    // Use a more efficient find method instead of iterating manually
    const book = books.find(book => book.bookID === bookID);
    if (book) {
      return book; // If a book is found, return it
    }
  }

  // If no book with the given ID is found, return null
  return null;
}
