const Card = ({ book }) => {
  const handleCardClick = () => {
    window.open(`https://www.google.com/search?q=${encodeURIComponent(book.title)}`, "_blank");
  };

  return (
    <div className="card" style={styles.card} onClick={handleCardClick}>
      <div style={styles.imgContainer}>
        <img 
          className="card-img-top" 
          src={book.image || "https://via.placeholder.com/150"} 
          alt="Book cover" 
          style={styles.img} 
        />
      </div>
      <div className="card-body" style={styles.cardBody}>
        <h5 className="card-title" style={styles.textOverflow}>{book.title}</h5>
        <p className="card-text" style={styles.textOverflow}>
          <strong>By: </strong>{book.authors?.[0] || "Not available"}
        </p>
        <p className="card-text">
          {book.pageCount || "Not available"} Pages | {book.categories}
        </p>
      </div>
    </div>
  );
};

const styles = {
  card: {
    width: '100%',
    minWidth: '225px',
    height: '600px', // Fixed total card height
    margin: '2%',
    display: 'flex',
    flexDirection: 'column',
    cursor: 'pointer', // Makes the card clickable
    textDecoration: 'none', // Removes default styling
    border: '1px solid #ddd',
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.2s ease-in-out',
  },
  imgContainer: {
    height: '500px', // Fixed height for image
    overflow: 'hidden',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f4f4f4',
  },
  img: {
    width: '100%',
    height: '100%',
    objectFit: 'cover', // Ensures the image covers the container
  },
  cardBody: {
    flexGrow: 1, 
    display: 'flex',
    flexDirection: 'column',
    padding: '10px',
    overflow: 'hidden',
  },
  textOverflow: {
    display: '-webkit-box',
    WebkitLineClamp: 2, // Limits text to 2 lines
    WebkitBoxOrient: 'vertical',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
  },
};

export default Card;
