import React, { useEffect, useState } from 'react';
import Navbar from '../Components/HomeNavbar';
import { useParams } from "react-router-dom";
import { TenCards } from '../Components/Card';
import { GetBookByID } from '../BookData';
import './Book.css';

function Book() {
  const { bookID } = useParams();
  const [book, setBook] = useState(null);

  useEffect(() => {
    console.log('Sending book id ' + bookID);
    const foundBook = GetBookByID(bookID);
    setBook(foundBook);
  }, [bookID]);

  if (!book) {
    return (
      <>
        <Navbar user="Guest" />
        <div style={{ padding: '2rem', color: 'white', backgroundColor: '#333' }}>
          Loading book data...
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar user="Guest" />
      <div style={{
        display: 'flex',
        minHeight: '100vh',
        backgroundColor: '#333',
        color: '#fff'
      }} id="body">
        {/* Left: Recommended cards */}
        <div style={{
          width: '33.33%',
          overflowY: 'auto',
          borderRight: '1px solid #444',
          padding: '1rem'
        }}>
          <h3 style={{ marginBottom: '1rem' }}>More in {book.categories}</h3>
          <TenCards genre={book.categories} />
        </div>

        {/* Right: Book details */}
        <div style={{
          width: '66.67%',
          overflowY: 'auto',
          padding: '2rem'
        }}>
          <h2 style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>{book.title}</h2>
          <h4 style={{ fontStyle: 'italic', marginBottom: '1rem' }}>
            {book.authors?.[0] || 'Unknown Author'}
          </h4>
          <p style={{ marginBottom: '0.5rem' }}>
            <strong>Genre:</strong> {book.categories}
          </p>
          <div style={{ marginTop: '1rem', lineHeight: '1.6' }}>
            <p><strong>Description:</strong></p>
            <p>{book.description || 'No description available.'}</p>
          </div>
        </div>
      </div>
    </>
  );
}

export default Book;
