import React, { useEffect, useState } from 'react';
import Navbar from '../Components/Navbar';
import { useUser } from '../UserContext';
import { useNavigate } from 'react-router-dom';
import { CompleteTransaction, GetCart, UpdateCartQuantity, RemoveFromCart } from '../Api.js';
import './Cart.css';

function Cart() {
    const { user } = useUser();
    const [cartItems, setCartItems] = useState([]);
    const [applyPoints, setApplyPoints] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (user?.id) {
            GetCart(user.id).then(setCartItems);
        }
    }, [user]);

    const handleQuantityChange = async (itemId, delta) => {
        const updated = cartItems.map(item =>
            item.id === itemId ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
        );
        setCartItems(updated);
        const target = updated.find(i => i.id === itemId);
        await UpdateCartQuantity(itemId, target.quantity);
    };

    const handleRemoveItem = async (itemId) => {
        setCartItems(cartItems.filter(item => item.id !== itemId));
        await RemoveFromCart(itemId);
    };

    const calculateTotalPrice = (item) => (item.price || 0) * (item.quantity || 1);
    const cartTotal = cartItems.reduce((sum, item) => sum + calculateTotalPrice(item), 0);
    const getDiscountedTotal = () => Math.max(0, cartTotal - (user?.points || 0) / 100);

    const handleCheckoutClick = () => setShowModal(true);
    const confirmPurchase = () => {
        CompleteTransaction(applyPoints);
        navigate('/');
    };

    return (
        <>
            <Navbar use="Guest" />
            <div style={{ display: 'flex', height: '100vh', overflow: 'hidden' }} id="body">
                <div className="cart-container" style={{ width: '75%', overflowY: 'auto', padding: '1rem' }}>
                    <h3 id="CardTitle">Your Cart</h3>
                    <ul style={{ listStyle: 'none', padding: 0 }}>
                        {cartItems.map(item => (
                            <li key={item.id} className="cart-item">
                                <p><strong>{item.title}</strong></p>
                                <div className="item-controls">
                                    <span>Price: ${item.price.toFixed(2)}</span>
                                    <span>Qty: {item.quantity}</span>
                                    <div className="quantity-buttons">
                                        <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
                                        <button onClick={() => handleQuantityChange(item.id, -1)}>−</button>
                                    </div>
                                </div>
                                <button onClick={() => handleRemoveItem(item.id)} className="remove-button">❌</button>
                            </li>
                        ))}
                    </ul>
                </div>

                <div className="cart-summary">
                    <h3 id="CardTitle">Cart Details</h3>
                    <h4>Total: ${applyPoints ? getDiscountedTotal().toFixed(2) : cartTotal.toFixed(2)}</h4>
                    <label className="points-label">
                        <input
                            type="checkbox"
                            checked={applyPoints}
                            onChange={() => setApplyPoints(!applyPoints)}
                            disabled={user?.points <= 0}
                        />
                        Apply {user?.points || 0} Points
                    </label>
                    <button className="checkout-button" onClick={handleCheckoutClick}>Checkout</button>
                </div>
            </div>

            {showModal && (
                <div className="modal-overlay" onClick={() => setShowModal(false)}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <h4>Checkout Summary</h4>
                        {cartItems.map(item => (
                            <p key={item.id}>{item.quantity} × {item.title} = ${calculateTotalPrice(item).toFixed(2)}</p>
                        ))}
                        <hr />
                        <p><strong>Total: ${applyPoints ? getDiscountedTotal().toFixed(2) : cartTotal.toFixed(2)}</strong></p>
                        {applyPoints && <p>Discount: ${((user?.points || 0) / 100).toFixed(2)}</p>}
                        <button onClick={confirmPurchase}>Confirm Purchase</button>
                    </div>
                </div>
            )}
        </>
    );
}

export default Cart;
