import React, { useEffect, useState } from 'react';
import './App.css';
import Card from './Card.jsx';
import Navbar from './Navbar.jsx';
import { fetchFictionBooks } from './api';

function App() {
  const [books, setBooks] = useState([]); // State to store the fetched books

  useEffect(() => {
    const fetchBooks = async () => {
      const fetchedBooks = await fetchFictionBooks(10); // Fetch 10 books
      setBooks(fetchedBooks); // Store books in state
    };

    fetchBooks(); // Call fetchBooks function when component mounts
  }, []); // Empty dependency array ensures this effect runs only once on mount

  return (
    <div className="App">
      <Navbar />
      {/* Render the books dynamically */}
      <div className="card-container">
        {books.length > 0 ? (
          books.map((book, index) => (
            <Card key={index} book={book} />
          ))
        ) : (
          <p>Loading books...</p> // Show a loading message until the books are fetched
        )}
      </div>
    </div>
  );
}

export default App;
