import React, { useEffect } from 'react';
import Navbar from '../Components/HomeNavbar';
import BookCarousel from '../Components/';
import { fetchBooksByGenre } from '../Api';

function Home() {
  useEffect(() => {
    document.body.style.backgroundColor = '#333';

    const allGenres = [
      "Fiction",
      "Mystery",
      "Biography",
      "Fantasy",
      "AutoBiography",
      "Nonfiction",
      "Thriller",
      "Science Fiction",
      "Poetry",
      "Novel"
    ];

    const fetchAllGenres = async () => {
      await Promise.all(allGenres.map(genre => fetchBooksByGenre(genre, 64)));
    };

    fetchAllGenres();
  }, []);

  return (
    <div className="App">
      <Navbar user={'Coy'} />
      <div id="body">
        <BookCarousel genre={"Fiction"} />
        <BookCarousel genre={"Mystery"} />
        <BookCarousel genre={"Biography"} />
        <BookCarousel genre={"Fantasy"} />
        <BookCarousel genre={"AutoBiography"} />
        <BookCarousel genre={"Nonfiction"} />
        <BookCarousel genre={"Thriller"} />
        <BookCarousel genre={"Science Fiction"} />
        <BookCarousel genre={"Poetry"} />
        <BookCarousel genre={"Novel"} />
      </div>
    </div>
  );
}

export default Home;
