import React from 'react';

const Card = ({ book }) => {
  return (
    <div className="card" style={styles.card}>
      <div style={styles.imgContainer}>
        <img 
          className="card-img-top" 
          src="https://m.media-amazon.com/images/I/91U6rc7u0yL.jpg" 
          alt="Card image cap" 
          style={styles.img} 
        />
      </div>
      <div className="card-body">
        <h5 className="card-title">{book.title}</h5>
        <h6 className="card-subtitle mb-2 text-muted">{book.subtitle}</h6>
        <p className="card-text">{book.desc}</p>
        <a href="#" className="btn btn-primary">Go somewhere</a>
      </div>
    </div>
  );
}

const styles = {
  card: {
    width: '21%',
    minWidth: '225px',
    margin: '2%',
    display: 'flex',
    flexDirection: 'column',
  },
  imgContainer: {
    height: '200px', // Fixed height for image container
    overflow: 'hidden', // Ensures image doesn't overflow the container
  },
  img: {
    width: '100%',  // Ensures the image fills the width of the container
    height: 'auto',  // Keeps the aspect ratio
    objectFit: 'contain',  // Scales the image down without cropping
  },
};

export default Card;
