import './Home.css';
import Navbar from '../Components/HomeNavbar';
import BookCarousel from '../Components/Carousels';
import React, { useEffect, useState } from 'react';
import { fetchBooksByGenre, booksByGenre } from '../Api';

const GENRES = [
  "Fiction",
  "Mystery",
  "Biography",
  "Fantasy",
  "AutoBiography",
  "Nonfiction",
  "Thriller",
  "Science Fiction",
  "Poetry",
  "Novel"
];

function Home() {
  const [loadedGenres, setLoadedGenres] = useState([]);

  useEffect(() => {
    document.body.style.backgroundColor = '#333';

    const loadBooksByGenre = async () => {
      for (const genre of GENRES) {
        // Check if books for this genre already exist
        const alreadyLoaded = booksByGenre[genre]?.length > 0;
        if (!alreadyLoaded) {
          try {
            await fetchBooksByGenre(genre, 32);
          } catch (err) {
            console.error(`Error loading ${genre}:`, err);
            continue;
          }
        }

        // Update loadedGenres state if not already included
        setLoadedGenres(prev =>
          prev.includes(genre) ? prev : [...prev, genre]
        );
      }
    };

    loadBooksByGenre();
  }, []);

  return (
    <div className="App">
      <Navbar user={'Guest'} />
      <div id="body">
        {GENRES.map(genre =>
          loadedGenres.includes(genre) ? (
            <BookCarousel key={genre} genre={genre} />
          ) : null
        )}
      </div>
    </div>
  );
}

export default Home;
