// Global variable to store books by category
export let booksByGenre = {};

// Function to clean book data
const cleanBookData = (book, category) => {
  return {
    title: book.title || "Unavailable",
    subtitle: book.subtitle || "Unavailable",
    authors: (book.authors && book.authors.length > 0) ? book.authors.join(", ") : "Unavailable",
    publisher: book.publisher || "Unavailable",
    description: book.description || "Unavailable",
    pageCount: book.pageCount || "Unavailable",
    categories: category || "Unavailable", // Set the category as provided
    rating: book.averageRating || "Unavailable",
    maturityRating: book.maturityRating || "Unavailable",
    image: book.imageLinks ? book.imageLinks.thumbnail : "Unavailable",
  };
};

// Fetch fiction books in batches
export const fetchFictionBooks = async (batchSize) => {
  let booksFetched = 0;
  let startIndex = 0;

  // Ensure that the category "Fiction" exists in the booksByCategory object
  if (!booksByCategory.Fiction) {
    booksByCategory.Fiction = [];
  }

  // Loop to fetch books in batches
  while (booksFetched < batchSize) {
    const remainingBooks = batchSize - booksFetched;
    const currentBatchSize = Math.min(remainingBooks, 40);

    let response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=fiction&maxResults=${currentBatchSize}&startIndex=${startIndex}`);
    let data = await response.json();

    if (!data.items || data.items.length === 0) {
      break;
    }

    data.items.forEach(item => {
      const bookInfo = item.volumeInfo;
      const cleanedBook = cleanBookData(bookInfo, "Fiction");
      console.log(cleanedBook)
      booksByCategory.Fiction.push(cleanedBook);
      booksFetched++;
    });

    // Move the startIndex forward for the next batch
    startIndex += currentBatchSize;
  }

  // Log the result for debugging
  console.log(booksByGenre);
  return booksByCategory;
};
