import './Home.css';
import Navbar from '../Components/HomeNavbar';
import BookCarousel from '../Components/Carousels';
import React, { useEffect, useState } from 'react';
import { fetchBooksByGenre} from '../Api';

const GENRES = [
  "Fiction",
  "Mystery",
  "Biography",
  "Fantasy",
  "AutoBiography",
  "Nonfiction",
  "Thriller",
  "Science Fiction",
  "Poetry",
  "Novel"
];

function Home() {
  const [loadedGenres, setLoadedGenres] = useState([]);

  useEffect(() => {
    document.body.style.backgroundColor = '#333';

    // Load genres one-by-one
    const loadBooksByGenre = async () => {
      for (const genre of GENRES) {
        try {
          await fetchBooksByGenre(genre); // This should populate booksByGenre[genre]
          setLoadedGenres(prev => [...prev, genre]); // Mark genre as loaded
        } catch (err) {
          console.error(`Error loading ${genre}:`, err);
        }
      }
    };

    loadBooksByGenre();
  }, []);

  return (
    <div className="App">
      <Navbar user={'Guest'} />
      <div id="body">
        {GENRES.map(genre =>
          loadedGenres.includes(genre) ? (
            <BookCarousel key={genre} genre={genre} />
          ) : null
        )}
      </div>
    </div>
  );
}

export default Home;
