import React from 'react';
import Navbar from '../Components/Navbar';
import { useUser } from '../UserContext';
import UserCart from '../Components/UserCart';
import './Cart.css';

function Cart() {
    const { user, setUser } = useUser();

    const books = Array.isArray(user?.books) ? user.books : [];

    const handleQuantityChange = (index, delta) => {
        const updatedBooks = [...user.books];
        const currentQty = updatedBooks[index].quantity || 1;
        const newQty = currentQty + delta;
        if (newQty >= 1) {
            updatedBooks[index].quantity = newQty;
            setUser({ ...user, books: updatedBooks });
        }
    };

    const calculateTotalPrice = (book) => {
        const qty = book.quantity || 1;
        const price = book.price || 0;
        return qty * price;
    };

    const handleRowClick = (book, index) => {
        console.log(`Row clicked: ${book.title} (index ${index})`);
        // You can use this for details popup or selection effect
    };

    const cartTotal = books.reduce((acc, book) => acc + calculateTotalPrice(book), 0);

    return (
        <>
            <Navbar use="Guest" />
            <div style={{ display: 'flex', height: '100vh', overflow: 'hidden' }} id="body">

                <div style={{ width: '75%', overflowY: 'auto', padding: '1rem', paddingTop: '1rem' }} className="cart-container">
                    <h3 id="CardTitle">Your Cart</h3>
                    <div className="cart-items">
                        <UserCart />
                    </div>
                </div>

                <div style={{ width: '25%', overflowY: 'auto', borderLeft: '1px solid #ccc' }}>
                    <div style={{ height: '100%', padding: '1rem', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                        <div>
                            <h3 id="CardTitle">Cart Details</h3>
                            <ul style={{ listStyle: 'none', padding: 0 }}>
                                {books.map((book, index) => (
                                    <li
                                        key={index}
                                        onClick={() => handleRowClick(book, index)}
                                        style={{
                                            marginBottom: '1rem',
                                            padding: '0.5rem',
                                            borderRadius: '6px',
                                            cursor: 'pointer',
                                            transition: 'background-color 0.2s ease',
                                            backgroundColor: '#f9f9f9',
                                        }}
                                        onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#e9ecef'}
                                        onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#f9f9f9'}
                                    >
                                        <p style={{ margin: 0, fontWeight: 'bold' }}>{book.title || 'Untitled'}</p>

                                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginTop: '0.25rem' }}>
                                            <label style={{ margin: 0 }}>Qty:</label>
                                            <button
                                                onClick={(e) => { e.stopPropagation(); handleQuantityChange(index, -1); }}
                                                style={{
                                                    width: '25px',
                                                    height: '25px',
                                                    borderRadius: '50%',
                                                    border: 'none',
                                                    backgroundColor: '#ccc',
                                                    fontWeight: 'bold',
                                                    cursor: 'pointer'
                                                }}
                                            >−</button>

                                            <span style={{ minWidth: '20px', textAlign: 'center' }}>
                                                {book.quantity || 1}
                                            </span>

                                            <button
                                                onClick={(e) => { e.stopPropagation(); handleQuantityChange(index, 1); }}
                                                style={{
                                                    width: '25px',
                                                    height: '25px',
                                                    borderRadius: '50%',
                                                    border: 'none',
                                                    backgroundColor: '#ccc',
                                                    fontWeight: 'bold',
                                                    cursor: 'pointer'
                                                }}
                                            >+</button>
                                        </div>

                                        <p style={{ margin: 0, marginTop: '0.25rem' }}>
                                            Total: ${calculateTotalPrice(book).toFixed(2)}
                                        </p>
                                    </li>
                                ))}
                            </ul>
                        </div>

                        <div style={{ borderTop: '1px solid #ccc', paddingTop: '1rem' }}>
                            <h4>Total: ${cartTotal.toFixed(2)}</h4>
                            <button
                                style={{
                                    marginTop: '1rem',
                                    padding: '0.75rem',
                                    width: '100%',
                                    backgroundColor: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    cursor: 'pointer',
                                    fontWeight: 'bold',
                                }}
                            >
                                Checkout
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Cart;
