import React, { useState, useEffect } from 'react';
import { login } from '../AuthenticateHelper'; 
import { useNavigate } from 'react-router-dom';
import { AuthenticateUser } from '../Api.js';
import { useUser } from '../UserContext.js';
import { auth, googleProvider, facebookProvider } from '../FireBase.js'; // Make sure you import these from your Firebase setup
import { signInWithPopup } from 'firebase/auth';
import './Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); 
  const navigate = useNavigate();
  const { setUser } = useUser();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const user = await login(email, password);
      AuthenticateUser(user.user.accessToken, setUser);
      navigate(-1);
    } catch (error) {
      setError('Login failed. Please check your email and password.');
      console.error('Login failed:', error.message);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;
      AuthenticateUser(user.accessToken, setUser);
      navigate(-1); 
    } catch (error) {
      console.error('Google login failed:', error.message);
      setError('Google login failed. Please try again.');
    }
  };

  const handleFacebookLogin = async () => {
    try {
      const result = await signInWithPopup(auth, facebookProvider);
      const user = result.user;
      AuthenticateUser(user.accessToken, setUser);
      navigate(-1);
    } catch (error) {
      console.error('Facebook login failed:', error.message);
      setError('Facebook login failed. Please try again.');
    }
  };

  useEffect(() => {
    document.body.classList.add('login-page-body');
    return () => document.body.classList.remove('login-page-body');
  }, []);

  return (
    <div className="main-container">
      <div className="form-container">
        <div className="form-body">
          <h2 className="title">Log in with</h2>

          <div className="social-login">
            <ul>
              <li className="google">
                <button onClick={handleGoogleLogin}>Google</button>
              </li>
              <li className="fb">
                <button onClick={handleFacebookLogin}>Facebook</button>
              </li>
            </ul>
          </div>

          <div className="_or">or</div>

          <form className="the-form" onSubmit={handleLogin}>
            <label htmlFor="email">Email</label>
            <input 
              type="email" 
              name="email" 
              id="email" 
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />

            <label htmlFor="password">Password</label>
            <input 
              type="password" 
              name="password" 
              id="password" 
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />

            <input type="submit" value="Log In" />
          </form>

          {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
        </div>

        <div className="form-footer">
          <div>
            <span>Don't have an account?</span> <a href="#">Sign Up</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
