const Card = ({ book }) => {

  return (
    <div className="card" style={styles.card}>
      <div style={styles.imgContainer}>
        <img 
          className="card-img-top" 
          src={book.image || "https://via.placeholder.com/150"} 
          alt="Card image cap" 
          style={styles.img} 
        />
      </div>
      <div className="card-body" style={styles.cardBody}>
        <h5 className="card-title">{book.title}</h5>
        <p className="card-text"><strong>By: </strong>{book.authors || "Not available"}</p>
        <p className="card-text">{book.pageCount || "Not available"} Pages, {book.categories}</p>
        <div style={styles.buttonContainer}>
          <a href="#" className="btn btn-primary">Go somewhere</a>
        </div>
      </div>
    </div>
  );
};

const styles = {
  card: {
    width: '100%',
    minWidth: '225px',
    height: '600px', // Fixed total card height
    margin: '2%',
    display: 'flex',
    flexDirection: 'column',
  },
  imgContainer: {
    height: '450px', // Increased image height
    overflow: 'hidden',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  img: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  cardBody: {
    flexGrow: 1, // Allows card body to take up available space
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between', // Ensures space between text and button
    paddingBottom: '10px', // Ensures space for the button
    overflow: 'hidden',
  },
  textContent: {
    flexGrow: 1, // Allows text to take available space
    overflow: 'hidden',
    display: '-webkit-box',
    WebkitLineClamp: 2, // Limits to 2 lines before cutoff
    WebkitBoxOrient: 'vertical',
    textOverflow: 'ellipsis',
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'center', // Centers the button
    paddingTop: '10px', // Adds spacing above the button
  }
};

export default Card;
