import React, { useEffect, useState } from 'react';
import { fetchFictionBooks } from './api';  // Assuming the API function to fetch books is already set up
import Card from './Card';

const BookCarousel = () => {
  const [books, setBooks] = useState([]);
  const [booksPerPage, setBooksPerPage] = useState(4);

  useEffect(() => {
    const loadBooks = async () => {
      const booksData = await fetchFictionBooks(16);
      setBooks(booksData.Fiction);
    };

    loadBooks();

    const handleResize = () => {
      if (window.innerWidth < 576) {
        setBooksPerPage(1);
      } else if (window.innerWidth < 768) {
        setBooksPerPage(2);
      } else if (window.innerWidth < 992) {
        setBooksPerPage(3);
      } else {
        setBooksPerPage(4);
      }
    };

    handleResize();

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const chunkBooks = (arr, size) => {
    const result = [];
    for (let i = 0; i < arr.length; i += size) {
      result.push(arr.slice(i, i + size));
    }
    return result;
  };

  const bookChunks = chunkBooks(books, booksPerPage); 

  return (
    <div className="container mt-5">
      <div id="bookCarousel" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">
          {bookChunks.map((chunk, index) => (
            <div className={`carousel-item ${index === 0 ? 'active' : ''}`} key={index}>
              <div className="row">
                {chunk.map((book, i) => (
                  <div className={`col-${12 / booksPerPage}`} key={i}>
                    <Card book={book} />
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#bookCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#bookCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>
  );
};

export default BookCarousel;
