import React, { useEffect, useState } from 'react';
import Navbar from '../Components/SearchNavbar';
import SearchList from '../Components/SearchList';
import SearchThroughBooks from '../Components'; // update this path if needed

function Search() {
  const [input, setInput] = useState('');
  const [filteredBooks, setFilteredBooks] = useState([]);

  useEffect(() => {
    document.body.style.backgroundColor = '#333';
  }, []);

  useEffect(() => {
    const results = SearchThroughBooks(input);
    setFilteredBooks(results);
  }, [input]);

  return (
    <div>
      <Navbar onSearchChange={setInput} />
      <div id="body">
        <SearchList books={filteredBooks} />
      </div>
    </div>
  );
}

export default Search;
