import React from 'react';

const Card = ({ book }) => {
  // Check if categories is a dictionary (object), if so, join the keys
  const categories = book.categories && typeof book.categories === 'object' 
    ? Object.keys(book.categories).join(", ") 
    : "Not available";

  return (
    <div className="card" style={styles.card}>
      <div style={styles.imgContainer}>
        <img 
          className="card-img-top" 
          src={book.image || "https://via.placeholder.com/150"} 
          alt="Card image cap" 
          style={styles.img} 
        />
      </div>
      <div className="card-body">
        <h5 className="card-title">{book.title}</h5>
        <p className="card-text"><strong>Authors: </strong>{book.authors || "Not available"}</p>
        <p className="card-text"><strong>Publisher: </strong>{book.publisher || "Not available"}</p>
        <p className="card-text"><strong>Page Count: </strong>{book.pageCount || "Not available"}</p>
        <p className="card-text"><strong>Categories: </strong>{categories}</p>
        <p className="card-text"><strong>Rating: </strong>{book.rating || "No rating"}</p>
        <p className="card-text"><strong>Maturity Rating: </strong>{book.maturityRating || "Not available"}</p>
        <a href="#" className="btn btn-primary">Go somewhere</a>
      </div>
    </div>
  );
};

const styles = {
  card: {
    width: '100%',
    minWidth: '225px',
    margin: '2%',
    display: 'flex',
    flexDirection: 'column',
  },
  imgContainer: {
    height: '350px', // Fixed height for image container
    overflow: 'hidden', // Ensures image doesn't overflow the container
  },
  img: {
    width: '100%',  // Ensures the image fills the width of the container
    height: 'auto',  // Keeps the aspect ratio
    objectFit: 'contain',  // Scales the image down without cropping
  },
};

export default Card;
