  const [debouncedInput, setDebouncedInput] = useState(input); // For debounced input

  // Debounce effect to wait until the user stops typing
  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      setDebouncedInput(input);
    }, 500); // Adjust debounce delay here (500ms)

    return () => clearTimeout(debounceTimer); // Clear the timeout if input changes again
  }, [input]);

