import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { booksByGenre } from "../Api";

const Card = ({ book }) => {
  const navigate = useNavigate();

  const handleCardClick = () => {
    navigate(`/book/${encodeURIComponent(book.id)}`);
  };

  const cardStyle = {
    width: "100%",
    minWidth: "225px",
    maxWidth: "300px",
    margin: "2%",
    display: "flex",
    flexDirection: "column",
    cursor: "pointer",
    textDecoration: "none",
    border: "1px solid #ddd",
    borderRadius: "0", // No rounded edges
    overflow: "visible",
    boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
    position: "relative",
    transition: "transform 0.2s ease-in-out",
    flexShrink: "0",
    zIndex: "100000",
  };

  const imgContainerStyle = {
    height: "500px",
    overflow: "hidden",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#f4f4f4",
  };

  const cardBodyStyle = {
    display: "flex",
    flexDirection: "column",
    padding: "10px",
    position: "absolute",
    bottom: "0", 
    left: "0",
    right: "0",
    backgroundColor: "rgba(0, 0, 0, 0.5)", 
    opacity: "0",
    transition: "opacity 0.5s ease", // Smooth opacity transition only
    color: "white",
    boxSizing: "border-box",
    zIndex: "10",
  };

  const handleHover = () => {
    const cardBody = document.querySelector(`.card-${book.id} .card-body`);
    cardBody.style.opacity = "1"; // Make it visible
  };

  const handleMouseLeave = () => {
    const cardBody = document.querySelector(`.card-${book.id} .card-body`);
    cardBody.style.opacity = "0"; // Make it invisible again
  };

  return (
    <div
      className={`card card-${book.id}`}
      style={cardStyle}
      onClick={handleCardClick}
      onMouseEnter={handleHover}
      onMouseLeave={handleMouseLeave}
    >
      <div className="img-container" style={imgContainerStyle}>
        <img
          className="card-img-top"
          src={book.image || "https://via.placeholder.com/150"}
          alt="Book cover"
          style={{ width: "100%", height: "100%", objectFit: "cover" }}
        />
      </div>
      <div className="card-body" style={cardBodyStyle}>
        <h5 className="card-title">{book.title}</h5>
        <p className="card-text">
          <strong>By: </strong>
          {(book.authors && book.authors.length > 0)
            ? book.authors[0]
            : "Not available"}
          <br />
          <strong>Genre: </strong>{book.categories}
        </p>
      </div>
    </div>
  );
};

export function TenCards({ genre }) {
  const [randomCards, setRandomCards] = useState([]);

  useEffect(() => {
    if (!genre || !booksByGenre[genre]) return;

    const allBooks = booksByGenre[genre];
    const shuffled = [...allBooks].sort(() => 0.5 - Math.random());
    const selected = shuffled.slice(0, 20);

    setRandomCards(selected);
  }, [genre]);

  return (
    <>
      <h3 style={{ color: "white", fontWeight: "bold", textShadow: "3px 3px 6px rgba(0, 0, 0, 0.7)", marginLeft: "30px" }}>
        More {genre}
      </h3>
      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", gap: "1rem", padding: "0 1rem", marginTop: "1rem", position: "relative" }}>
        {randomCards.map((book) => (
          <Card book={book} key={book.id} />
        ))}
      </div>
    </>
  );
}

export default Card;
