import React from 'react';
import { useLocation } from 'react-router-dom';

export default function Cart() {
  const location = useLocation();
  const user = location.state?.user;
  const [books, setUser] = React.useState(user?.books || []);
  const [applyPoints, setApplyPoints] = React.useState(false);

  const handleQuantityChange = (index, amount) => {
    const updatedBooks = [...books];
    updatedBooks[index].quantity = Math.max(1, (updatedBooks[index].quantity || 1) + amount);
    setUser({ ...user, books: updatedBooks });
  };

  const cartTotal = books.reduce((acc, book) => acc + (book.price || 0) * (book.quantity || 1), 0);

  const getDiscountedTotal = () => {
    if (!user?.points || user.points <= 0) return cartTotal;
    const discount = Math.min(user.points, cartTotal);
    return cartTotal - discount;
  };

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#1a1a1a', color: 'white' }}>
      <div style={{ width: '75%', padding: '2rem', overflowY: 'auto' }}>
        <h1 id="CardTitle">Your Cart</h1>
        <div className="card-container">
          {books.map((book, index) => (
            <div key={index} className="card" style={{ backgroundColor: '#fff' }}>
              <div className="img-container">
                <img
                  src={book.image || 'https://via.placeholder.com/150'}
                  alt={book.title || 'Book cover'}
                  className="card-img-top"
                />
              </div>
              <div className="card-body">
                <h5 className="card-title">{book.title || 'Unavailable'}</h5>
                <p className="card-text">{book.description || 'No description available.'}</p>
                <p className="card-text">Price: ${book.price?.toFixed(2) || '0.00'}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div
        style={{
          width: '25%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100%',
          borderLeft: '1px solid #ccc',
          padding: '1rem',
          boxSizing: 'border-box',
        }}
      >
        <div style={{ width: '100%' }}>
          <h3 id="CardTitle">Cart Details</h3>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {books.map((book, index) => (
              <li
                key={index}
                style={{
                  marginBottom: '1rem',
                  padding: '0.5rem',
                  borderRadius: '6px',
                  backgroundColor: '#f9f9f9',
                  position: 'relative',
                }}
              >
                <p style={{ margin: 0, fontWeight: 'bold', marginBottom: '6px' }}>{book.title || 'Untitled'}</p>
                <div style={{ display: 'flex', alignItems: 'center', gap: '12px', fontSize: '16px' }}>
                  <span>Price: ${book.price?.toFixed(2) || '0.00'}</span>
                  <span>Qty: {book.quantity || 1}</span>
                  <div style={{ display: 'flex', flexDirection: 'column', marginLeft: '-4px' }}>
                    <button onClick={() => handleQuantityChange(index, 1)} style={{ border: 'none', background: 'none', fontSize: '14px', cursor: 'pointer', marginBottom: '-8px' }}>+</button>
                    <button onClick={() => handleQuantityChange(index, -1)} style={{ border: 'none', background: 'none', fontSize: '14px', cursor: 'pointer', marginTop: '-4px' }}>−</button>
                  </div>
                </div>
                <button
                  onClick={() => {
                    const updatedBooks = books.filter((_, i) => i !== index);
                    setUser({ ...user, books: updatedBooks });
                  }}
                  style={{
                    position: 'absolute',
                    top: '8px',
                    right: '10px',
                    background: 'none',
                    border: 'none',
                    fontSize: '16px',
                    color: '#a00',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                  }}
                >
                  ❌
                </button>
              </li>
            ))}
          </ul>

          <div
            style={{
              borderTop: '1px solid #ccc',
              padding: '1rem',
              backgroundColor: '#333',
              boxShadow: '0 -2px 5px rgba(0,0,0,0.05)',
              color: 'white',
              marginTop: '1rem',
            }}
          >
            <h4>
              Total: ${applyPoints ? getDiscountedTotal().toFixed(2) : cartTotal.toFixed(2)}
            </h4>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginTop: '1rem' }}>
              <label htmlFor="applyPoints" style={{ display: 'flex', alignItems: 'center', gap: '8px', fontSize: '14px' }}>
                <input
                  type="checkbox"
                  id="applyPoints"
                  name="applyPoints"
                  style={{ transform: 'scale(1.2)' }}
                  checked={applyPoints}
                  onChange={() => setApplyPoints(!applyPoints)}
                  disabled={user?.points <= 0}
                />
                Apply {user?.points || 0} Points
              </label>
            </div>
            <button
              style={{
                marginTop: '1rem',
                padding: '0.75rem',
                width: '100%',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontWeight: 'bold',
              }}
            >
              Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
