import React from 'react';
import defaultProfile from './assets/defaultProfile.png';
import cartIcon from './assets/icons8-shopping-cart-48.png';

const Navbar = ({ user = "Guest" }) => {
  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark" style={styles.navbar}>
      <a className="navbar-brand" href="#">3rd & Chester</a>
      <div className="container-fluid d-flex justify-content-between">
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <ul className="navbar-nav ms-auto" id="list">
          <li className="nav-item active">
            <a href="#" className="d-flex flex-column align-items-center" style={styles.link}>
              <span style={styles.cartText}>View Cart</span>
              <img src={cartIcon} alt="Cart" style={styles.cartIcon} />
            </a>
          </li>
        </ul>

        <a href="#" className="d-flex text-decoration-none">
          <div style={styles.nameContainer}>
            {user !== 'Guest' ? (
              <>
                <span style={styles.welcomeText}>Welcome,</span>
                <span style={styles.userName}>{user}</span>
              </>
            ) : (
              <>
                <span style={styles.welcomeText}>Click to</span>
                <span style={styles.userName}>Login</span>
              </>
            )}
          </div>
          <img 
            src={defaultProfile}
            alt="Profile" 
            style={styles.profileImage} 
          />
        </a>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    backgroundColor: '#333',
    padding: '0',
  },
  profileImage: {
    width: '60px',
    height: '60px',
    borderRadius: '50%',
    objectFit: 'cover',
    clipPath: 'circle(38% at 50% 50%)',
    marginRight: '10px',
  },
  nameContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  },
  welcomeText: {
    fontSize: '14px',
    fontWeight: 'normal',
    color: '#fff',
  },
  userName: {
    fontSize: '16px',
    fontWeight: 'bold',
    color: '#fff',
  },
  cartText: {
    color: '#fff',
    fontSize: '14px',
    marginBottom: '1px',
  },
  cartIcon: {
    width: '30px',
    height: '30px',
  },
  link: {
    textDecoration: 'none',  // Remove the underline
  }
};

export default Navbar;
