import { useUser } from '../UserContext';
import Card from './Card';

function UserCart() {
  const { user } = useUser();

  if (!user || !Array.isArray(user.books) || user.books.length === 0) {
    return <div>No books in cart.</div>;
  }

  return (
    <div>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {user.books.map((book, index) => (
          <li key={index} style={{
            display: 'flex',
            alignItems: 'flex-start',
            justifyContent: 'space-between',
            marginBottom: '1.5rem',
            borderBottom: '1px solid #ccc',
            paddingBottom: '1.5rem'
          }}>
            <div style={{ flex: 1, paddingRight: '1rem' }}>
              <h4 style={{ marginBottom: '0.5rem' }}>{book.title || "Untitled"}</h4>
              <p style={{ margin: 0 }}><strong>Author:</strong> {book?.authors || "Unknown Author"}</p>
              <p style={{ margin: 0 }}><strong>Publisher:</strong> {book?.publisher || "Unknown"}</p>
              <p style={{ margin: 0 }}><strong>Pages:</strong> {book?.pageCount || "N/A"}</p>
              <p style={{ margin: 0 }}><strong>Genre:</strong> {book?.categories || "N/A"}</p>
              <p style={{ margin: 0 }}><strong>Rating:</strong> {book?.rating || "Unrated"}</p>
              <p style={{ margin: 0 }}><strong>Price:</strong> ${book?.price?.toFixed(2) || "0.00"}</p>
              <p style={{ margin: 0 }}><strong>Description:</strong> ${book?.price?.toFixed(2) || "0.00"}</p>
            </div>

            <div style={{ flexShrink: 0, marginLeft: '2rem' }}>
              <Card book={book} />
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UserCart;
