import { booksByGenre } from '../Api.js'
import Card from './Card.jsx'
import React, { useState, useEffect } from 'react';

const SearchList = ({ input }) => {
    const [filteredBooks, setFilteredBooks] = useState([]);

    useEffect(() => {
        if (!input) return;

        const flatBooks = Object.values(booksByGenre).flat();

        const scoredBooks = flatBooks.map(book => ({
          ...book,
          score: getSimilarityScore(input, book)
        }));

        scoredBooks.sort((a, b) => b.score - a.score);
        setFilteredBooks(scoredBooks);
      }, [input]);

    //{filteredBooks.map((book, index) => (
     //   <Card key={index} book={book} />
     // ))}

    return (
      <div className="book-grid">
        {console.log(filteredBooks)}
      </div>
    );
  };

export const SearchThroughBooks = (input) => {
    const scoredBooks = [];

    Object.keys(booksByGenre).forEach((genre) => {
      booksByGenre[genre].forEach((book) => {
        const score = getSimilarityScore(input, book); // your custom scoring logic
        scoredBooks.push({ ...book, score });
      });
    });

    scoredBooks.sort((a, b) => b.score - a.score);

    return scoredBooks;
  };

  function getSimilarityScore(input, book) {
    const normalize = str => {
      if (Array.isArray(str)) {
        // Join array elements into a string and normalize
        return str.join(', ').toLowerCase().trim();
      }

      // Ensure it's a string, otherwise treat it as an empty string
      return (typeof str === 'string' ? str : '').toLowerCase().trim();
    };

    const inputNormalized = normalize(input);
    const title = normalize(book.title);  // Ensure this is a string
    const genre = normalize(book.categories);  // Ensure this is a string
    const authors = normalize(book.authors);  // Ensure this is a string (handles arrays)

    const titleScore = partialRatio(inputNormalized, title);
    const genreScore = partialRatio(inputNormalized, genre);
    const authorScore = partialRatio(inputNormalized, authors);

    // Weighted average (tweak as needed)
    return (0.5 * titleScore) + (0.3 * authorScore) + (0.2 * genreScore);
  }  


function partialRatio(a, b) {
    if (a.length > b.length) [a, b] = [b, a];

    let maxScore = 0;
    for (let i = 0; i <= b.length - a.length; i++) {
      const window = b.substr(i, a.length);
      const score = levenshteinSimilarity(a, window);
      maxScore = Math.max(maxScore, score);
    }
    return maxScore;
  }

  function levenshteinSimilarity(a, b) {
    const distance = levenshtein(a, b);
    const maxLen = Math.max(a.length, b.length);
    return maxLen === 0 ? 1 : (1 - distance / maxLen);
  }

  function levenshtein(a, b) {
    const matrix = Array.from({ length: a.length + 1 }, () =>
      Array(b.length + 1).fill(0)
    );

    for (let i = 0; i <= a.length; i++) matrix[i][0] = i;
    for (let j = 0; j <= b.length; j++) matrix[0][j] = j;

    for (let i = 1; i <= a.length; i++) {
      for (let j = 1; j <= b.length; j++) {
        matrix[i][j] = Math.min(
          matrix[i - 1][j] + 1,
          matrix[i][j - 1] + 1,
          matrix[i - 1][j - 1] + (a[i - 1] === b[j - 1] ? 0 : 1)
        );
      }
    }

    return matrix[a.length][b.length];
  }


export default SearchList;
