import React, { useEffect, useState } from 'react';
import Navbar from '../Components/SearchNavbar';
import SearchList from '../Components/SearchList';
import { SearchThroughBooks } from '../Components/SearchList'; // Make sure the import is correct

function Search() {
  const [input, setInput] = useState('');
  const [filteredBooks, setFilteredBooks] = useState([]);

  useEffect(() => {
    document.body.style.backgroundColor = '#333';
  }, []);

  // Update filtered books based on the input
  useEffect(() => {
    if (input.trim()) {
      console.log(input);
      const results = SearchThroughBooks(input);  // Call search function based on input
      setFilteredBooks(results);
    } else {
      setFilteredBooks([]);  // Clear results if the input is empty
    }
  }, [input]);

  return (
    <div>
      <Navbar onSearchChange={setInput} />
      <div id="body">
        <SearchList books={filteredBooks} />
      </div>
    </div>
  );
}

export default Search;
