import { booksByGenre, genreIDs } from '../Api.js';
import Card from './Card.jsx';
import React, { useState, useEffect } from 'react';
import '../Pages/Search.css';

const ITEMS_PER_PAGE = 20;

const BooksByGenre = ({ genreID }) => {
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const genre = genreIDs.find(g => g.id === genreID);
    if (genre) {
      // Access the books for the genre from booksByGenre using genre.genre
      const genreBooks = booksByGenre[genre.genre] || [];
      // Sort the books alphabetically by title
      const sortedBooks = genreBooks.sort((a, b) => a.title.localeCompare(b.title));

      setFilteredBooks(sortedBooks);
      setCurrentPage(1); // Reset to the first page
    } else {
      setFilteredBooks([]);
    }
  }, [genreID]);

  const totalPages = Math.ceil(filteredBooks.length / ITEMS_PER_PAGE);
  const currentBooks = filteredBooks.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  const handlePrev = () => {
    setCurrentPage((prev) => Math.max(prev - 1, 1));
  };

  const handleNext = () => {
    setCurrentPage((prev) => Math.min(prev + 1, totalPages));
  };

  return (
    <div id="body" className="search-results-container">
      <h2 className="results-title">Books in this Genre</h2>

      <div className="book-grid">
        {currentBooks.map((book, index) => (
          <div key={index} className="book-card-wrapper">
            <Card book={book} />
          </div>
        ))}
      </div>

      {totalPages > 1 && (
        <div className="pagination-controls">
          <div className="pagination-buttons">
            <button onClick={handlePrev} disabled={currentPage === 1}>
              Previous
            </button>
            <button onClick={handleNext} disabled={currentPage === totalPages}>
              Next
            </button>
          </div>
          <div className="pagination-page">
            Page {currentPage} of {totalPages}
          </div>
        </div>
      )}
    </div>
  );
};

export default BooksByGenre;
