import React, { useEffect, useState } from 'react';
import './Home.css';
import Navbar from '../Components/HomeNavbar';
import BookCarousel from '../Components/Carousels';
import { fetchAllGenres } from '../Api';

function Home() {
  const [dataLoaded, setDataLoaded] = useState(false);

  useEffect(() => {
    document.body.style.backgroundColor = '#333';

    const loadGenres = async () => {
      try {
        await fetchAllGenres();
        setDataLoaded(true); // ✅ signal to children
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };

    loadGenres();
  }, []);

  const allGenres = [
    "Fiction", "Mystery", "Biography", "Fantasy", "AutoBiography",
    "Nonfiction", "Thriller", "Science Fiction", "Poetry", "Novel"
  ];

  return (
    <div className="App">
      <Navbar user={'Coy'} />
      <div id="body">
        {dataLoaded && allGenres.map((genre) => (
          <BookCarousel key={genre} genre={genre} />
        ))}
      </div>
    </div>
  );
}

export default Home;
